		WHERE CDCALENDAR = @vCdCalendar AND
		FREQUENCY = 'W'
	
	OPEN CURSORVCWEEK
		FETCH NEXT FROM CURSORVCWEEK INTO @vDateDayWeekFind
		
		WHILE @@FETCH_STATUS = 0 
		BEGIN
