CREATE FUNCTION [dbo].[GET_CALCULATE_PRIORITY_COLOR]
(
	-- Add the parameters for the function here
	@vPriority NUMERIC(17,2),
	@vCdOrderType VARCHAR(1),
	@vYellowZoneStart NUMERIC(5,2),
	@vRedZoneStart NUMERIC(5,2)
)
RETURNS VARCHAR(50)
--WITH ENCRYPTION
AS
BEGIN
	DECLARE @vResult VARCHAR(50)
	--BEGIN TRY
	IF(@vCdOrderType='S')
	BEGIN
		IF (@vPriority IS NULL)
		BEGIN
			SET @vResult = 'White'
		END 
		ELSE IF (@vPriority <= 0)
		BEGIN
			SET @vResult = 'Blue'
		END
			
		ELSE IF ( (@vPriority > 0) AND @vPriority < @vYellowZoneStart)
		BEGIN
			SET @vResult = 'Green'
		END
			
		ELSE IF ( (@vPriority >= @vYellowZoneStart) AND (@vPriority < @vRedZoneStart) )
		BEGIN
			SET @vResult = 'Yellow'
		END
			
		ELSE IF ( (@vPriority >= @vRedZoneStart) AND (@vPriority < 100) )
		BEGIN
			SET @vResult = 'Red'
		END
			
		ELSE IF (@vPriority >= 100)
		BEGIN
			SET @vResult = 'Black'
		END 			
		ELSE
		BEGIN
			SET @vResult = 'Error'
		END
			 
		
	END
	ELSE
	BEGIN
		IF @vPriority < 0
			SET @vResult = 'Cyan'
		ELSE IF (@vPriority >= 0 AND @vPriority <= @vYellowZoneStart)
			SET @vResult = 'Green'
		ELSE IF (@vPriority > @vYellowZoneStart AND @vPriority <= @vRedZoneStart)
			SET @vResult = 'Yellow'
		ELSE IF (@vPriority > @vRedZoneStart AND @vPriority < 100)
			SET @vResult = 'Red'
		ELSE
			SET @vResult = 'Black'
	END
	
	RETURN @vResult
	--END TRY
	
