	BEGIN 
		UPDATE TFSCM_CLI_MESSAGESNOTIFICATIONS SET MESSAGE = @ovaMessage WHERE ID_MESSAGESNOTIFICATIONS = @ID_MESSAGESNOTIFICATIONS ; 
