				SET @ORIGIN_BUFFER_FAMILY = SELECT FAMILY FROM dbo.TFSCM_CLI_BUFFER AS a WHERE a.SKU = SKU AND a.LOCATION = ORIGIN_LOCATION;*/
				-- ---------------------------------------------------------------------------


				SET @TOTAL_CONSOLIDATED_INPUTS = 0; 

