							  ,ORIGIN_NET_INVENTORY     = @ORIGIN_NET_INVENTORY
							  ,ORIGIN_BUFFER_FAMILY     = @ORIGIN_BUFFER_FAMILY

						WHERE SKU = @SKU AND LOCATION = @LOCATION
					END 
