CREATE PROCEDURE [dbo].[GENERATE_ADJUSTMENT_SUGGESTIONS_HISTORY]
	/* Input Parameters */
	@ivaManagedBy VARCHAR(50),
    @ivaLocation  VARCHAR(50),
    @ivaSku  VARCHAR(50),
    @ovaResult VARCHAR(1) OUTPUT,
    @ovaMessage NVARCHAR(MAX) OUTPUT 
	-- WITH ENCRYPTION
AS
	
