					  ' FROM TFSCM_CLI_PRODUCTION_ORDERS ORDERS '+
					  '		 JOIN TFSCM_CLI_PRODUCTION_FAMILY FAMILY ON(ORDERS.CDFAMILY = FAMILY.CDFAMILY) '+
