CREATE PROCEDURE [dbo].[SP_REMOVE_VIEW_INDICATOR] 
	/* Input Parameters */
	@cdView   	 INT, 
	@cdIndicator NUMERIC(4,0),
	/* Output Parameters */
	@ovaResult   VARCHAR(1)   OUTPUT,
	@ovaMessage  VARCHAR(MAX) OUTPUT 
--WITH ENCRYPTION
AS
