																				END) 
													       AND ICT.COLUMN_NAME = IC.COLUMN_NAME
														   AND ICT.COLUMN_NAME NOT LIKE '%_STR') HAS_TMP
											      FROM INFORMATION_SCHEMA.COLUMNS IC 
											     WHERE IC.TABLE_NAME = (CASE WHEN ([CDSECTION] = 1) THEN
																		    'TFSCM_CLI_PURCHASE_ORDERS'
