		   AND [LOCATION_CHG] = @GinOldLocation;
		   
		-- -----------------------------------------------------------------------------------
		
		UPDATE [dbo].[TFSCM_CLI_BUFFER_HISTORY]
		   SET [SKU] = @GinNewSku
		 WHERE [SKU]	  = @GinOldSku
		   AND [LOCATION] = @GinOldLocation;

		UPDATE [dbo].[TFSCM_CLI_BUFFER_REPLENISHMENT_HISTORY]
		   SET [SKU] = @GinNewSku
		 WHERE [SKU]        = @GinOldSku
		   AND [CDLOCATION] = @GinOldLocation;

